example.code:
use MariaDB as db, System;

// Connect to the database
db.connect(
    "jdbc:mariadb://localhost/my-database" ::
    "?user=root" ::
    "&password=super1secret"
);

var object = db.getObject("people", {"id": 5346});

var json = System.toJSON(object, true);
System.print(json);

use MariaDB as db, System;

// Connect to the database
db.connect(
    "jdbc:mariadb://localhost/my-database" ::
    "?user=root" ::
    "&password=super1secret"
);

var example = {
    "firstName": "John",
    "lastName": "Doe"
};

var row = db.getObject("people", example);

var json = System.toJSON(row, true);
System.print(json);
